import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [messages, setMessages] = useState([
    { role: 'bot', content: 'Hi! I’m your shopping assistant. How can I help?' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5000/api/chatbot', { message: input });
      const botMessage = { role: 'bot', content: res.data.reply };
      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      setMessages(prev => [...prev, { role: 'bot', content: 'Sorry, something went wrong.' }]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div style={styles.container}>
      <div style={styles.chatbox}>
        {messages.map((msg, i) => (
          <div key={i} style={msg.role === 'user' ? styles.userMsg : styles.botMsg}>
            {msg.content}
          </div>
        ))}
        {loading && <div style={styles.botMsg}>Typing...</div>}
      </div>

      <div style={styles.inputArea}>
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
          placeholder="Ask me about products..."
          style={styles.input}
        />
        <button onClick={sendMessage} style={styles.button}>Send</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    position: 'fixed',
    bottom: 30,
    right: 30,
    width: 350,
    background: '#fff',
    borderRadius: 10,
    boxShadow: '0 0 10px rgba(0,0,0,0.2)',
    fontFamily: 'sans-serif',
    overflow: 'hidden',
    zIndex: 9999,
  },
  chatbox: {
    height: 300,
    overflowY: 'auto',
    padding: 10,
    background: '#f9f9f9',
  },
  userMsg: {
    textAlign: 'right',
    margin: '5px 0',
    padding: '8px 12px',
    background: '#daf1ff',
    borderRadius: '10px 0 10px 10px',
    display: 'inline-block',
  },
  botMsg: {
    textAlign: 'left',
    margin: '5px 0',
    padding: '8px 12px',
    background: '#eee',
    borderRadius: '0 10px 10px 10px',
    display: 'inline-block',
  },
  inputArea: {
    display: 'flex',
    borderTop: '1px solid #ddd',
  },
  input: {
    flex: 1,
    padding: 10,
    border: 'none',
    outline: 'none',
  },
  button: {
    padding: '0 16px',
    border: 'none',
    background: '#007bff',
    color: '#fff',
    cursor: 'pointer',
  }
};

export default Chatbot;
