import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext'; // Assuming you store token here

function Recommendations() {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { token, user } = useContext(AuthContext); // token & userId from auth

  useEffect(() => {
    if (!user || !token) return;

    const fetchRecommendations = async () => {
      setLoading(true);
      try {
        const res = await axios.get(
          `http://localhost:5000/api/recommendations/user`, // or /:userId if your API requires param
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setRecommendations(res.data);
        setError(null);
      } catch (err) {
        setError('Failed to load recommendations');
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, [user, token]);

  if (loading) return <p>Loading recommendations...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (recommendations.length === 0) return <p>No recommendations found.</p>;

  return (
    <div>
      <h3>Recommended for You</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px' }}>
        {recommendations.map((p) => (
          <div key={p._id} style={{ border: '1px solid #ccc', padding: '10px', width: '180px' }}>
            <img src={p.image} alt={p.name} style={{ width: '100%' }} />
            <h4>{p.brand}</h4>
            <p>{p.name}</p>
            <p>${p.price.toFixed(2)}</p>
            <p>{p.category}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Recommendations;
