import React, { useState } from 'react';
import axios from 'axios';

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [error, setError] = useState(null);
  const [mode, setMode] = useState('semantic'); // 'semantic' or 'keyword'

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) {
      onResults([]);
      return;
    }

    const baseUrl =
      mode === 'semantic'
        ? `http://localhost:5000/api/search/semantic?q=${encodeURIComponent(query)}`
        : `http://localhost:5000/api/products/search?query=${encodeURIComponent(query)}`;

    try {
      setError(null);
      const res = await axios.get(baseUrl);
      onResults(res.data);
    } catch (err) {
      console.error('Search failed:', err);
      setError('Search failed. Please try again.');
      onResults([]);
    }
  };

  return (
    <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
      <input
        type="text"
        placeholder="Search products, e.g. 'running shoes waterproof'"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ width: '300px', padding: '8px' }}
      />
      <select
        value={mode}
        onChange={(e) => setMode(e.target.value)}
        style={{ marginLeft: '10px', padding: '8px' }}
      >
        <option value="semantic">Semantic Search</option>
        <option value="keyword">Keyword Search</option>
      </select>
      <button type="submit" style={{ marginLeft: '10px', padding: '8px 12px' }}>
        Search
      </button>
      {error && <p style={{ color: 'red', marginTop: '8px' }}>{error}</p>}
    </form>
  );
}

export default SearchBar;
