import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, PointElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Title, Tooltip, Legend);

const AdminDashboard = () => {
  const [users, setUsers] = useState(0);
  const [products, setProducts] = useState(0);
  const [orders, setOrders] = useState(0);
  const [revenue, setRevenue] = useState(0);
  const [salesData, setSalesData] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await axios.get('/api/admin/stats');
        setUsers(res.data.totalUsers);
        setProducts(res.data.totalProducts);
        setOrders(res.data.totalOrders);
        setRevenue(res.data.totalRevenue);
      } catch (err) {
        console.error('Error fetching stats:', err);
      }
    };

    const fetchSalesData = async () => {
      try {
        const res = await axios.get('/api/admin/sales?days=7');
        setSalesData(res.data); // expected: [{ _id: '2025-07-11', total: 100 }, ...]
      } catch (err) {
        console.error('Error fetching sales data:', err);
      }
    };

    fetchStats();
    fetchSalesData();
  }, []);

  const chartData = {
    labels: salesData.map(day => day._id),
    datasets: [
      {
        label: 'Revenue ($)',
        data: salesData.map(day => day.total),
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: false,
        tension: 0.3,
        pointBackgroundColor: '#007bff'
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Sales over the last 7 days'
      },
      tooltip: {
        enabled: true
      }
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Date'
        }
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Revenue ($)'
        }
      }
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">Admin Dashboard</h2>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-10">
        <div className="bg-white p-4 rounded shadow text-center">
          <h3 className="text-sm font-semibold">Users</h3>
          <p className="text-2xl">{users}</p>
        </div>
        <div className="bg-white p-4 rounded shadow text-center">
          <h3 className="text-sm font-semibold">Products</h3>
          <p className="text-2xl">{products}</p>
        </div>
        <div className="bg-white p-4 rounded shadow text-center">
          <h3 className="text-sm font-semibold">Orders</h3>
          <p className="text-2xl">{orders}</p>
        </div>
        <div className="bg-white p-4 rounded shadow text-center">
          <h3 className="text-sm font-semibold">Revenue</h3>
          <p className="text-2xl">${revenue.toFixed(2)}</p>
        </div>
      </div>

      <div className="bg-white p-6 rounded shadow overflow-x-auto">
        <h3 className="text-lg font-semibold mb-4">Sales Chart (Last 7 Days)</h3>
        <div style={{ minWidth: '600px', height: '400px' }}>
          <Line data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
