import { useContext, useState } from 'react';
import axios from 'axios';
import { CartContext } from '../context/CartContext';
import { AuthContext } from '../context/AuthContext';

function Cart() {
  const { cart, removeFromCart, clearCart } = useContext(CartContext);
  const { user } = useContext(AuthContext);
  const [orderPlaced, setOrderPlaced] = useState(false);

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
  if (!user) {
    alert('You must be logged in to place an order.');
    return;
  }

  try {
    const token = localStorage.getItem('token');
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    await axios.post(
      'http://localhost:5000/api/orders',
      { cart, total },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    clearCart();
    setTimeout(() => setOrderPlaced(true), 100);
  } catch (err) {
    alert('Error placing order. Try again.');
    console.error(err);
  }
};

  return (
    <div>
      <h2>Your Cart</h2>

      {orderPlaced && (
        <div style={{ padding: '1rem', border: '2px solid green', marginBottom: '1rem' }}>
          âœ… Order placed successfully! Thank you for shopping with us.
        </div>
      )}

      {cart.length === 0 && !orderPlaced ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div key={item._id} style={{ border: '1px solid #ccc', margin: '10px 0', padding: '10px' }}>
              <h4>{item.name}</h4>
              <img src={item.image} alt={item.name} style={{ width: '150px' }} />
              <p>Price: ${item.price.toFixed(2)}</p>
              <p>Quantity: {item.quantity}</p>
              <p>Subtotal: ${(item.price * item.quantity).toFixed(2)}</p>
              <button onClick={() => removeFromCart(item._id)}>Remove</button>
            </div>
          ))}
          <h3>Total: ${total.toFixed(2)}</h3>
          <button onClick={clearCart}>Clear Cart</button>{' '}
          <button onClick={handleCheckout} style={{ background: 'green', color: 'white' }}>
            Checkout
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
