import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [data, setData] = useState({
    totalUsers: 0,
    totalOrders: 0,
    totalRevenue: 0,
    totalProducts: 0,
  });

  useEffect(() => {
    axios.get('/api/admin/overview')
      .then(res => setData(res.data))
      .catch(err => console.error('Admin overview fetch failed:', err));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Admin Dashboard</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-blue-100 p-4 rounded shadow text-center">
          <h3 className="text-lg">Users</h3>
          <p className="text-2xl font-bold">{data.totalUsers}</p>
        </div>
        <div className="bg-green-100 p-4 rounded shadow text-center">
          <h3 className="text-lg">Orders</h3>
          <p className="text-2xl font-bold">{data.totalOrders}</p>
        </div>
        <div className="bg-yellow-100 p-4 rounded shadow text-center">
          <h3 className="text-lg">Revenue</h3>
          <p className="text-2xl font-bold">${data.totalRevenue.toFixed(2)}</p>
        </div>
        <div className="bg-purple-100 p-4 rounded shadow text-center">
          <h3 className="text-lg">Products</h3>
          <p className="text-2xl font-bold">{data.totalProducts}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
