import { useEffect, useState } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

function AdminOrders() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');
  let user;
  try {
    user = jwtDecode(token);
  } catch {
    user = null;
  }

  useEffect(() => {
    const fetchOrders = async () => {
      if (!user || user.role !== 'admin') {
        setError('Access denied: Admins only');
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/orders/admin/all-orders', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Error fetching all orders:', err);
        setError('Failed to fetch orders');
      }
    };

    fetchOrders();
  }, [user, token]);

  return (
    <div>
      <h2>All Orders (Admin)</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order) => (
          <div key={order._id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '15px' }}>
            <p><strong>User:</strong> {order.userId?.email || 'Unknown'}</p>
            <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>
            <p><strong>Total:</strong> ${order.totalAmount?.toFixed(2)}</p>

            <h4>Items:</h4>
            {order.products?.map((item, idx) => (
              <div key={idx} style={{ marginLeft: '20px' }}>
                <p>{item.name} — ${item.price?.toFixed(2)} × {item.quantity}</p>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
}

export default AdminOrders;
