import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend, Filler);

const AdminDashboard = () => {
  const [overview, setOverview] = useState(null);
  const [salesData, setSalesData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [overviewRes, salesRes] = await Promise.all([
          axios.get('http://localhost:5000/api/admin/overview'),
          axios.get('http://localhost:5000/api/admin/sales?days=7'),
        ]);
        setOverview(overviewRes.data);
        setSalesData(salesRes.data);
      } catch (err) {
        setError('Failed to load admin data.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading dashboard data...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  const chartData = {
    labels: salesData.map(item => item._id),
    datasets: [
      {
        label: 'Sales',
        data: salesData.map(item => item.total),
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'teal',
        fill: true,
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Admin Dashboard</h2>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-gray-100 p-4 rounded-lg shadow text-center">
          <h3 className="text-lg font-medium">Users</h3>
          <p className="text-xl font-bold">{overview.totalUsers}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded-lg shadow text-center">
          <h3 className="text-lg font-medium">Orders</h3>
          <p className="text-xl font-bold">{overview.totalOrders}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded-lg shadow text-center">
          <h3 className="text-lg font-medium">Products</h3>
          <p className="text-xl font-bold">{overview.totalProducts}</p>
        </div>
        <div className="bg-gray-100 p-4 rounded-lg shadow text-center">
          <h3 className="text-lg font-medium">Revenue</h3>
          <p className="text-xl font-bold">${overview.totalRevenue.toFixed(2)}</p>
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow overflow-x-auto">
        <h3 className="text-lg font-medium mb-4">Sales (Last 7 Days)</h3>
  <div style={{ minWidth: '600px', height: '400px' }}>
    <Line data={chartData} options={chartOptions} />
  </div>
</div>

    </div>
  );
};

export default AdminDashboard;
