import { useContext, useState } from 'react';
import axios from 'axios';
import { CartContext } from '../context/CartContext';
import { AuthContext } from '../context/AuthContext';
import CheckoutForm from '../components/CheckoutForm'; // ✅ Import CheckoutForm
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe('pk_test_Your_Publishable_Key');

function Cart() {
  const { cart, removeFromCart, clearCart } = useContext(CartContext);
  const { user } = useContext(AuthContext);
  const [orderPlaced, setOrderPlaced] = useState(false);

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div>
      <h2>Your Cart</h2>

      {orderPlaced && (
        <div style={{ padding: '1rem', border: '2px solid green', marginBottom: '1rem' }}>
          ✅ Order placed successfully! Thank you for shopping with us.
        </div>
      )}

      {cart.length === 0 && !orderPlaced ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cart.map((item) => (
            <div key={item._id} style={{ border: '1px solid #ccc', margin: '10px 0', padding: '10px' }}>
              <h4>{item.name}</h4>
              <img src={item.image} alt={item.name} style={{ width: '150px' }} />
              <p>Price: ${item.price.toFixed(2)}</p>
              <p>Quantity: {item.quantity}</p>
              <p>Subtotal: ${(item.price * item.quantity).toFixed(2)}</p>
              <button onClick={() => removeFromCart(item._id)}>Remove</button>
            </div>
          ))}

          <h3>Total: ${total.toFixed(2)}</h3>
          {/* <button onClick={clearCart}>Clear Cart</button> */}

          {/* ✅ Stripe Elements Checkout */}
          <Elements stripe={stripePromise}>
            <CheckoutForm
              amount={total}
              cart={cart}
              onSuccess={() => {
                clearCart();
                setOrderPlaced(true);
              }}
            />
          </Elements>
        </div>
      )}
    </div>
  );
}

export default Cart;
