import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hi! I am your shopping assistant. How can I help you today?' },
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(scrollToBottom, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { from: 'user', text: input };
    setMessages(prev => [...prev, userMsg]);
    setInput('');

    try {
      const res = await axios.post('http://localhost:5000/api/chatbot', { message: input });
      const botMsg = { from: 'bot', text: res.data.reply };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      setMessages(prev => [...prev, { from: 'bot', text: 'Sorry, something went wrong.' }]);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div style={{
      position: 'fixed', bottom: 20, right: 20,
      width: 320, height: 400,
      boxShadow: '0 0 10px rgba(0,0,0,0.3)',
      borderRadius: 8, backgroundColor: 'white',
      display: 'flex', flexDirection: 'column',
    }}>
      <div style={{
        flex: 1, padding: 10, overflowY: 'auto', borderBottom: '1px solid #ddd',
      }}>
        {messages.map((m, i) => (
          <div
            key={i}
            style={{
              textAlign: m.from === 'bot' ? 'left' : 'right',
              margin: '8px 0',
            }}
          >
            <span style={{
              display: 'inline-block',
              padding: '8px 12px',
              borderRadius: 16,
              backgroundColor: m.from === 'bot' ? '#f1f0f0' : '#4caf50',
              color: m.from === 'bot' ? 'black' : 'white',
            }}>
              {m.text}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div style={{ padding: 10 }}>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          rows={2}
          style={{ width: '100%', resize: 'none' }}
          placeholder="Type your message..."
        />
        <button
          onClick={sendMessage}
          style={{
            marginTop: 8, width: '100%', padding: 8,
            backgroundColor: '#4caf50', color: 'white', border: 'none', borderRadius: 4,
            cursor: 'pointer',
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
}
