import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';

function OrderHistory() {
  const { user } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Error fetching order history:', err);
      } finally {
        setLoading(false);
      }
    };

    if (user) fetchOrders();
  }, [user]);

  if (loading) return <p>Loading orders...</p>;

  if (orders.length === 0) return <p>No orders yet.</p>;

  return (
    <div>
      <h2>Your Order History</h2>
      {orders.map((order) => (
        <div key={order._id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '15px' }}>
          <p><strong>Order Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>
          <p><strong>Status:</strong> {order.status}</p>
          <p><strong>Tracking Number:</strong> {order.trackingNumber || 'N/A'}</p>
          <p><strong>Estimated Delivery:</strong> {order.estimatedDeliveryDate ? new Date(order.estimatedDeliveryDate).toLocaleDateString() : 'N/A'}</p>
          <p><strong>Total:</strong> ${order.totalAmount ? order.totalAmount.toFixed(2) : '0.00'}</p>
          <h4>Items:</h4>
          {order.products.length > 0 ? (
            order.products.map((item, idx) => (
              <div key={idx} style={{ marginLeft: '20px', marginBottom: '10px' }}>
                <p><strong>{item.brand}</strong></p>
                <p>{item.name || 'Unnamed Item'} — ${item.price?.toFixed(2) || '0.00'} × {item.quantity || 1}</p>
              </div>
            ))
          ) : (
            <p>No items found in this order.</p>
          )}
          <h5>Status History:</h5>
          {order.history && order.history.length > 0 ? (
            <ul>
              {order.history.map((h, i) => (
                <li key={i}>
                  {h.status} at {new Date(h.updatedAt).toLocaleString()}
                </li>
              ))}
            </ul>
          ) : (
            <p>No status history available.</p>
          )}
        </div>
      ))}
    </div>
  );
}

export default OrderHistory;
